import { ref, computed, onMounted, reactive } from 'vue';
import { professionalAPI } from '@/services/api';
import { useRouter } from 'vue-router';
export default {
  name: 'ProfessionalDashboard',
  setup() {
    const router = useRouter();
    // State
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const professionalName = ref(user.name || 'Professional');
    const activeTab = ref('pending');
    const loading = ref(true);
    const serviceRequests = ref([]);
    const profile = ref({
      service_type: '',
      experience: 0,
      average_rating: 0,
      total_reviews: 0,
      is_approved: false,
      description: '',
      email: user.email || '',
      phone: '',
      date_created: new Date().toISOString()
    });
    const reviews = ref([]);
    const showDetailsModal = ref(false);
    const showProfileModal = ref(false);
    const selectedRequest = ref(null);
    const isAvailable = ref(true);
    const updatingProfile = ref(false);
    const profileForm = reactive({
      description: '',
      phone: '',
      experience: 0
    });
    const availableRequests = ref([]);

    // Computed properties
    const pendingRequests = computed(() => serviceRequests.value.filter(req => req.status === 'pending'));
    const inProgressRequests = computed(() => serviceRequests.value.filter(req => req.status === 'in_progress' || req.status === 'assigned'));
    const completedRequests = computed(() => serviceRequests.value.filter(req => req.status === 'completed' || req.status === 'closed'));
    const filteredRequests = computed(() => {
      if (activeTab.value === 'available') {
        return availableRequests.value;
      } else if (activeTab.value === 'pending') {
        return pendingRequests.value;
      } else if (activeTab.value === 'assigned') {
        return inProgressRequests.value;
      } else if (activeTab.value === 'completed') {
        return completedRequests.value;
      }
      return [];
    });

    // Methods
    const fetchData = async () => {
      loading.value = true;
      try {
        // Check if user has valid authentication first
        const token = localStorage.getItem('token');
        if (!token) {
          console.error('No authentication token found');
          router.push('/login');
          return;
        }

        // Get user info from localStorage
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        if (!user || user.role !== 'professional') {
          console.error('User is not a professional');
          router.push('/login');
          return;
        }

        // Try to fetch data with better error handling
        try {
          const profileData = await professionalAPI.getProfile();
          profile.value = {
            ...profile.value,
            ...profileData
          };

          // Initialize profile form with current values
          profileForm.description = profileData.description || '';
          profileForm.phone = profileData.phone || '';
          profileForm.experience = profileData.experience || 0;
        } catch (error) {
          console.error('Failed to load profile data:', error);
          // Continue with default profile values
        }
        try {
          const requestsData = await professionalAPI.getAssignments();
          serviceRequests.value = requestsData || [];
        } catch (error) {
          console.error('Failed to load assignments:', error);
          serviceRequests.value = [];
        }

        // Fetch reviews with error handling
        fetchReviews();

        // Fetch available requests with error handling
        fetchAvailableRequests();
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      } finally {
        loading.value = false;
      }
    };
    const fetchReviews = async () => {
      try {
        const reviewsData = await professionalAPI.getReviews();
        reviews.value = reviewsData || [];
      } catch (error) {
        console.error('Error fetching reviews:', error);
        reviews.value = [];
      }
    };
    const fetchAvailableRequests = async () => {
      try {
        console.log('Fetching available service requests...');
        const response = await professionalAPI.getAvailableRequests();
        availableRequests.value = response || [];
        console.log(`Loaded ${availableRequests.value.length} available requests`);
      } catch (error) {
        console.error('Error fetching available requests:', error);
        availableRequests.value = [];
      }
    };
    const acceptRequest = async (requestId, closeModal = false) => {
      try {
        const result = await professionalAPI.updateStatus(requestId, 'in_progress');

        // If this was an available request, refresh that list
        if (activeTab.value === 'available') {
          fetchAvailableRequests();
        }
        await fetchData();
        if (closeModal) {
          showDetailsModal.value = false;
        }
        alert('Service request accepted successfully. You can find it in the "In Progress" tab.');
      } catch (error) {
        console.error('Error accepting request:', error);
        alert('Failed to accept request: ' + (error.message || 'Unknown error'));
      }
    };
    const rejectRequest = async (requestId, closeModal = false) => {
      if (confirm('Are you sure you want to reject this request?')) {
        try {
          await professionalAPI.updateStatus(requestId, 'rejected');
          await fetchData();
          if (closeModal) {
            showDetailsModal.value = false;
          }
        } catch (error) {
          console.error('Error rejecting request:', error);
        }
      }
    };
    const completeRequest = async (requestId, closeModal = false) => {
      try {
        const request = serviceRequests.value.find(r => r.id === requestId);

        // Check if location exit is confirmed
        if (!request.exited_location) {
          // Ask for confirmation to exit location
          if (confirm('Have you exited the customer location? This is required before completing the service.')) {
            // If confirmed, mark as exited first
            await professionalAPI.confirmLocationExit(requestId);
            request.exited_location = true;
          } else {
            // If not confirmed, don't complete
            alert('You must confirm you have exited the customer location before completing the service.');
            return;
          }
        }

        // Now complete the request
        await professionalAPI.updateStatus(requestId, 'completed');
        await fetchData();
        if (closeModal) {
          showDetailsModal.value = false;
        }
        alert('Service request marked as completed successfully.');
      } catch (error) {
        console.error('Error completing request:', error);
        alert('Failed to complete the request: ' + (error.message || 'Unknown error'));
      }
    };
    const viewRequestDetails = request => {
      // Add the exited_location property if needed
      selectedRequest.value = {
        ...request,
        exited_location: request.exited_location || false
      };
      showDetailsModal.value = true;
    };
    const updateAvailability = async () => {
      try {
        await professionalAPI.updateAvailability(isAvailable.value);
        alert(`You are now ${isAvailable.value ? 'available' : 'unavailable'} for new service requests.`);
      } catch (error) {
        console.error('Error updating availability:', error);
        // Revert to previous state if there was an error
        isAvailable.value = !isAvailable.value;
      }
    };
    const updateProfile = async () => {
      try {
        updatingProfile.value = true;
        await professionalAPI.updateProfile(profileForm);

        // Update local profile data
        profile.value.description = profileForm.description;
        profile.value.phone = profileForm.phone;
        profile.value.experience = profileForm.experience;
        alert('Profile updated successfully!');
      } catch (error) {
        console.error('Error updating profile:', error);
        alert('Failed to update profile. Please try again.');
      } finally {
        updatingProfile.value = false;
      }
    };
    const formatDate = dateString => {
      if (!dateString) return 'N/A';
      const date = new Date(dateString);
      return date.toLocaleString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    };
    const getStatusBadgeClass = status => {
      const classes = {
        pending: 'badge bg-warning',
        rejected: 'badge bg-danger',
        in_progress: 'badge bg-primary',
        assigned: 'badge bg-info',
        completed: 'badge bg-success',
        closed: 'badge bg-secondary'
      };
      return classes[status] || 'badge bg-secondary';
    };
    const getEmptyStateMessage = () => {
      if (activeTab.value === 'available') {
        return 'No service requests available in your area. Check back later for new opportunities.';
      } else if (activeTab.value === 'pending') {
        return 'No new service requests available at the moment. Check back later.';
      } else if (activeTab.value === 'assigned') {
        return 'You have no active service assignments.';
      } else {
        return 'You have not completed any service requests yet.';
      }
    };

    // Lifecycle hooks
    onMounted(fetchData);
    return {
      professionalName,
      activeTab,
      loading,
      serviceRequests,
      profile,
      reviews,
      pendingRequests,
      inProgressRequests,
      completedRequests,
      filteredRequests,
      showDetailsModal,
      showProfileModal,
      selectedRequest,
      isAvailable,
      profileForm,
      updatingProfile,
      acceptRequest,
      rejectRequest,
      completeRequest,
      viewRequestDetails,
      updateAvailability,
      updateProfile,
      formatDate,
      getStatusBadgeClass,
      getEmptyStateMessage,
      availableRequests,
      fetchAvailableRequests
    };
  }
};
