import { ref, computed, onMounted, reactive } from 'vue';
import { professionalAPI } from '@/services/api';
import { useRouter } from 'vue-router';
export default {
  name: 'ProfessionalDashboard',
  data() {
    return {
      professionalName: '',
      activeTab: 'pending',
      loading: true,
      serviceRequests: [],
      profile: {
        service_type: '',
        experience: 0,
        average_rating: 0,
        total_reviews: 0,
        is_approved: false,
        description: '',
        email: '',
        phone: '',
        date_created: new Date().toISOString()
      },
      reviews: [],
      showDetailsModal: false,
      showProfileModal: false,
      selectedRequest: null,
      isAvailable: true,
      updatingProfile: false,
      profileForm: {
        description: '',
        phone: '',
        experience: 0
      },
      availableRequests: [],
      error: null
    };
  },
  computed: {
    pendingRequests() {
      return this.serviceRequests.filter(req => req.status === 'pending') || [];
    },
    inProgressRequests() {
      return this.serviceRequests.filter(req => req.status === 'in_progress' || req.status === 'assigned') || [];
    },
    completedRequests() {
      return this.serviceRequests.filter(req => req.status === 'completed' || req.status === 'closed') || [];
    },
    filteredRequests() {
      if (this.activeTab === 'available') {
        return this.availableRequests;
      } else if (this.activeTab === 'pending') {
        return this.pendingRequests;
      } else if (this.activeTab === 'assigned') {
        return this.inProgressRequests;
      } else if (this.activeTab === 'completed') {
        return this.completedRequests;
      }
      return [];
    }
  },
  created() {
    this.fetchData();
  },
  methods: {
    checkAuth() {
      const token = localStorage.getItem('token');
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      if (!token) {
        console.log('No authentication token found - redirecting to login');
        // Add a small delay to prevent navigation errors during component initialization
        setTimeout(() => {
          this.$router.push('/login');
        }, 100);
        return false;
      }
      if (user.role !== 'professional') {
        console.log('User is not a professional - redirecting');
        setTimeout(() => {
          this.$router.push('/login');
        }, 100);
        return false;
      }
      this.professionalName = user.name || 'Professional';
      return true;
    },
    async fetchData() {
      try {
        // Only proceed if authentication check passed
        if (!this.checkAuth()) {
          return;
        }
        this.loading = true;
        this.error = null;

        // Only fetch data if we have a token
        const token = localStorage.getItem('token');
        if (!token) {
          throw new Error('Authentication required');
        }
        await Promise.all([this.fetchProfile(), this.fetchRequests()]);
      } catch (error) {
        console.error('Dashboard data fetch error:', error);
        this.error = error.message || 'Failed to load dashboard data';

        // If token is missing or invalid, redirect to login
        if (error.message.includes('authentication') || error.message.includes('token')) {
          setTimeout(() => this.$router.push('/login'), 1000);
        }
      } finally {
        this.loading = false;
      }
    },
    async fetchProfile() {
      try {
        const profileData = await professionalAPI.getProfile();
        this.profile = {
          ...this.profile,
          ...profileData
        };

        // Initialize profile form with current values
        this.profileForm.description = profileData.description || '';
        this.profileForm.phone = profileData.phone || '';
        this.profileForm.experience = profileData.experience || 0;
      } catch (error) {
        console.error('Failed to load profile data:', error);
        // Continue with default profile values
      }
    },
    async fetchRequests() {
      try {
        const requestsData = await professionalAPI.getAssignments();
        this.serviceRequests = requestsData || [];
      } catch (error) {
        console.error('Failed to load assignments:', error);
        this.serviceRequests = [];
      }

      // Fetch reviews with error handling
      this.fetchReviews();

      // Fetch available requests with error handling
      this.fetchAvailableRequests();
    },
    async fetchReviews() {
      try {
        const reviewsData = await professionalAPI.getReviews();
        this.reviews = reviewsData || [];
      } catch (error) {
        console.error('Error fetching reviews:', error);
        this.reviews = [];
      }
    },
    async fetchAvailableRequests() {
      try {
        console.log('Fetching available service requests...');
        // Updated to explicitly fetch ALL available service requests from service_requests table
        const response = await professionalAPI.getAvailableRequests();

        // Add dummy data if needed for testing
        if (!response || response.length === 0) {
          console.log('No requests from API, adding sample data for development');
          this.availableRequests = this.generateSampleRequests();
        } else {
          this.availableRequests = response || [];

          // Add profile images if missing
          this.availableRequests = this.availableRequests.map(req => {
            if (!req.customer.profile_image) {
              req.customer.profile_image = 'https://via.placeholder.com/40';
            }
            if (!req.customer.address_area && req.pin_code) {
              req.customer.address_area = `Area ${req.pin_code.substring(0, 3)}`;
            }
            return req;
          });
        }

        // Sort by created date (newest first)
        this.availableRequests.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
        console.log(`Loaded ${this.availableRequests.length} available requests`);
      } catch (error) {
        console.error('Error fetching available requests:', error);
        // Provide sample data for development/testing
        this.availableRequests = this.generateSampleRequests();
      }
    },
    // Generate sample data for testing
    generateSampleRequests() {
      const serviceTypes = ['Plumbing Repair', 'Electrical Work', 'House Cleaning', 'Appliance Repair', 'Pest Control', 'Painting'];
      const customerNames = ['John Doe', 'Jane Smith', 'Robert Johnson', 'Emily Clark', 'Michael Brown', 'Sophia Wilson'];
      const areas = ['North', 'South', 'East', 'West', 'Central'];
      const instructions = ['Please arrive before noon', 'The issue is in the kitchen', 'I need help with multiple fixtures', 'Emergency repair needed', 'Please bring all necessary tools', 'Call before arriving'];
      return Array(6).fill().map((_, i) => ({
        id: 1000 + i,
        status: 'pending',
        service: {
          id: 100 + i,
          name: serviceTypes[i % serviceTypes.length],
          base_price: 500 + i * 100
        },
        customer: {
          id: 200 + i,
          customer_name: customerNames[i % customerNames.length],
          phone: `+91 98765${10000 + i}`,
          address: `123 Main St, ${areas[i % areas.length]} Area`,
          address_area: `${areas[i % areas.length]} Area`,
          profile_image: `https://randomuser.me/api/portraits/${i % 2 ? 'women' : 'men'}/${i + 1}.jpg`
        },
        pin_code: `${400000 + i * 1000}`,
        special_instructions: instructions[i % instructions.length],
        created_at: new Date(Date.now() - i * 24 * 60 * 60 * 1000).toISOString(),
        // Each one day apart
        exited_location: false
      }));
    },
    async acceptRequest(requestId, closeModal = false) {
      try {
        const request = this.availableRequests.find(r => r.id === requestId) || this.serviceRequests.find(r => r.id === requestId);
        if (!request) {
          throw new Error('Request not found');
        }

        // First check if the request is already assigned to someone else
        if (request.professional_id && request.professional_id !== this.profile.id) {
          alert('This request has already been assigned to another professional.');
          return;
        }

        // Accept the request
        await professionalAPI.updateStatus(requestId, 'in_progress');

        // Refresh data after accepting
        await this.fetchData();
        if (closeModal) {
          this.showDetailsModal = false;
        }

        // Show success notification
        alert('Service request accepted successfully. You can find it in the "In Progress" tab.');
      } catch (error) {
        console.error('Error accepting request:', error);
        alert('Failed to accept request: ' + (error.message || 'Unknown error'));
      }
    },
    async rejectRequest(requestId, closeModal = false) {
      if (confirm('Are you sure you want to reject this request?')) {
        try {
          await professionalAPI.updateStatus(requestId, 'rejected');

          // Add reason for rejection if in a modal
          if (closeModal) {
            const reason = prompt('Please provide a reason for rejection (optional):');
            if (reason) {
              await professionalAPI.addRejectionReason(requestId, reason);
            }
          }
          await this.fetchData();
          if (closeModal) {
            this.showDetailsModal = false;
          }
          alert('Service request has been rejected.');
        } catch (error) {
          console.error('Error rejecting request:', error);
          alert('Failed to reject request: ' + (error.message || 'Unknown error'));
        }
      }
    },
    async completeRequest(requestId, closeModal = false) {
      try {
        const request = this.serviceRequests.find(r => r.id === requestId);
        if (!request) {
          throw new Error('Request not found');
        }

        // Check if location exit is confirmed
        if (!request.exited_location) {
          // Ask for confirmation to exit location
          if (confirm('Have you exited the customer location? This is required before completing the service.')) {
            // If confirmed, mark as exited first
            await professionalAPI.confirmLocationExit(requestId);
            request.exited_location = true;
          } else {
            // If not confirmed, don't complete
            alert('You must confirm you have exited the customer location before completing the service.');
            return;
          }
        }

        // Now complete the request
        await professionalAPI.updateStatus(requestId, 'completed');
        await this.fetchData();
        if (closeModal) {
          this.showDetailsModal = false;
        }
        alert('Service request marked as completed successfully. The customer will be notified to close the service.');
      } catch (error) {
        console.error('Error completing request:', error);
        alert('Failed to complete the request: ' + (error.message || 'Unknown error'));
      }
    },
    // Add the missing method for rejection reason
    async addRejectionReason(requestId, reason) {
      try {
        await professionalAPI.addRejectionReason(requestId, reason);
      } catch (error) {
        console.error('Error adding rejection reason:', error);
      }
    },
    viewRequestDetails(request) {
      // Add the exited_location property if needed
      this.selectedRequest = {
        ...request,
        exited_location: request.exited_location || false
      };
      this.showDetailsModal = true;
    },
    async updateAvailability() {
      try {
        await professionalAPI.updateAvailability(this.isAvailable);
        alert(`You are now ${this.isAvailable ? 'available' : 'unavailable'} for new service requests.`);
      } catch (error) {
        console.error('Error updating availability:', error);
        // Revert to previous state if there was an error
        this.isAvailable = !this.isAvailable;
      }
    },
    async updateProfile() {
      try {
        this.updatingProfile = true;
        await professionalAPI.updateProfile(this.profileForm);

        // Update local profile data
        this.profile.description = this.profileForm.description;
        this.profile.phone = this.profileForm.phone;
        this.profile.experience = this.profileForm.experience;
        alert('Profile updated successfully!');
      } catch (error) {
        console.error('Error updating profile:', error);
        alert('Failed to update profile. Please try again.');
      } finally {
        this.updatingProfile = false;
      }
    },
    formatDate(dateString) {
      if (!dateString) return 'N/A';
      const date = new Date(dateString);
      return date.toLocaleString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    },
    getStatusBadgeClass(status) {
      const classes = {
        pending: 'badge bg-warning',
        rejected: 'badge bg-danger',
        in_progress: 'badge bg-primary',
        assigned: 'badge bg-info',
        completed: 'badge bg-success',
        closed: 'badge bg-secondary'
      };
      return classes[status] || 'badge bg-secondary';
    },
    getEmptyStateMessage() {
      if (this.activeTab === 'available') {
        return 'No service requests available in your area. Check back later for new opportunities.';
      } else if (this.activeTab === 'pending') {
        return 'No new service requests available at the moment. Check back later.';
      } else if (this.activeTab === 'assigned') {
        return 'You have no active service assignments.';
      } else {
        return 'You have not completed any service requests yet.';
      }
    }
  },
  mounted() {
    // Don't call fetchData here since we're doing it in created/checkAuth
  }
};
