import { ref, onMounted, computed } from 'vue';
import { Modal } from 'bootstrap';
import Chart from 'chart.js/auto';
import { adminAPI } from '@/services/api';
export default {
  name: 'AdminDashboard',
  setup() {
    // State
    const stats = ref({
      totalUsers: 0,
      activeServices: 0,
      pendingApprovals: 0,
      blockedUsers: 0
    });
    const users = ref([]);
    const services = ref([]);
    const currentTab = ref('users');
    const userSearch = ref('');
    const serviceSearch = ref('');
    const userTypeFilter = ref('');
    const serviceForm = ref({
      name: '',
      description: '',
      base_price: '',
      timeRequired: ''
    });
    const editingService = ref(null);
    const selectedUser = ref(null);
    const requestsChart = ref(null);
    const usersChart = ref(null);
    const loading = ref(false);
    const error = ref(null);
    const isDeleting = ref(false);

    // Tabs configuration
    const tabs = [{
      id: 'users',
      name: 'Users Management',
      icon: 'fas fa-users'
    }, {
      id: 'services',
      name: 'Services',
      icon: 'fas fa-tools'
    }, {
      id: 'analytics',
      name: 'Analytics',
      icon: 'fas fa-chart-bar'
    }];

    // Computed properties
    const pendingApprovals = computed(() => users.value.filter(user => user.role === 'professional' && !user.is_approved));
    const filteredUsers = computed(() => {
      return users.value.filter(user => {
        const matchesSearch = (user.name || '').toLowerCase().includes(userSearch.value.toLowerCase()) || (user.email || '').toLowerCase().includes(userSearch.value.toLowerCase());
        if (userTypeFilter.value === 'pending') {
          return matchesSearch && user.role === 'professional' && !user.is_approved;
        } else {
          const matchesType = !userTypeFilter.value || user.role === userTypeFilter.value;
          return matchesSearch && matchesType;
        }
      });
    });
    const filteredServices = computed(() => {
      return services.value.filter(service => service.name.toLowerCase().includes(serviceSearch.value.toLowerCase()));
    });

    // Methods
    const loadDashboardData = async () => {
      loading.value = true;
      error.value = null;
      try {
        console.log('Loading dashboard data...');
        // Debug token information
        const token = localStorage.getItem('token');
        if (!token) {
          throw new Error('No authentication token found. Please login again.');
        }
        console.log(`Token exists: ${token.substring(0, 10)}...`);

        // Load users data first - if this fails, we know authentication is an issue
        const usersData = await adminAPI.getUsers();
        console.log(`Loaded ${usersData.length} users successfully`);
        users.value = usersData;
        try {
          // Now try to load services with detailed logging
          console.log('Attempting to load services...');
          const servicesData = await adminAPI.getServices();
          console.log('Raw services data:', servicesData);
          console.log(`Loaded ${servicesData.length} services successfully`);
          services.value = servicesData;
        } catch (servicesError) {
          console.error('Error loading services:', servicesError);
          error.value = `Failed to load services: ${servicesError.message}`;
          // Don't fail completely, just show users without services
          services.value = [];
        }

        // Update dashboard stats
        updateStats();
        loading.value = false;
      } catch (err) {
        console.error('Error loading dashboard data:', err);
        error.value = err.message || 'Failed to load dashboard data. Please try again.';
        loading.value = false;
      }
    };
    const downloadCSV = () => {
      // Implement CSV download logic here
      fetch('/api/export').then(response => response.json()).then(data => {
        window.location.href = `/api/csv_result/${data.id}`;
      });
    };
    const updateStats = () => {
      stats.value = {
        totalUsers: users.value.length,
        activeServices: services.value.filter(s => s.is_active !== false).length,
        pendingApprovals: pendingApprovals.value.length,
        blockedUsers: users.value.filter(u => u.is_blocked).length
      };
    };
    const showNewServiceModal = () => {
      editingService.value = null;
      serviceForm.value = {
        name: '',
        description: '',
        base_price: '',
        timeRequired: ''
      };
      new Modal(document.getElementById('serviceModal')).show();
    };
    const editService = service => {
      editingService.value = service;
      serviceForm.value = {
        ...service
      };
      new Modal(document.getElementById('serviceModal')).show();
    };
    const saveService = async () => {
      try {
        if (editingService.value) {
          await adminAPI.update_service(editingService.value.id, serviceForm.value);
        } else {
          await adminAPI.createService(serviceForm.value);
        }
        await loadDashboardData();
        new Modal(document.getElementById('serviceModal')).hide();
      } catch (error) {
        console.error('Error saving service:', error);
      }
    };
    const handleServiceDelete = async serviceId => {
      try {
        const result = await adminAPI.deleteService(serviceId);
        if (result.wasDeactivated) {
          this.$toast.info(result.message);
          // Update the service status in the list instead of removing it
          const serviceIndex = services.value.findIndex(s => s.id === serviceId);
          if (serviceIndex !== -1) {
            services.value[serviceIndex].is_active = false;
            services.value[serviceIndex].status = 'inactive';
          }
        } else {
          this.$toast.success('Service deleted successfully');
          // Remove the service from the list
          services.value = services.value.filter(s => s.id !== serviceId);
        }
      } catch (error) {
        console.error('Failed to delete service:', error);
        this.$toast.error(error.message || 'Failed to delete service');
      }
    };
    const approveUser = async userId => {
      try {
        if (confirm('Are you sure you want to approve this professional?')) {
          await adminAPI.approveUser(userId);
          alert('Professional approved successfully');
          await loadDashboardData();
        }
      } catch (error) {
        console.error('Error approving user:', error);
        alert('Failed to approve professional: ' + (error.message || 'Unknown error'));
      }
    };
    const toggleUserBlock = async user => {
      try {
        if (user.is_blocked) {
          // Unblock user
          if (confirm('Are you sure you want to unblock this user?')) {
            await adminAPI.unblockUser(user.id);
            alert('User unblocked successfully');
          }
        } else {
          // Block user with reason
          const reason = prompt('Please enter a reason for blocking this user:', 'Violation of terms of service');
          if (reason) {
            await adminAPI.blockUser(user.id, reason);
            alert('User blocked successfully');
          }
        }
        await loadDashboardData();
      } catch (error) {
        console.error('Error toggling user block status:', error);
        alert('Operation failed: ' + error.message);
      }
    };
    const viewUserDetails = user => {
      selectedUser.value = user;
      new Modal(document.getElementById('userDetailsModal')).show();
    };
    const initCharts = () => {
      // Requests Chart
      const requestsCtx = document.querySelector('#requestsChart');
      if (requestsCtx) {
        requestsChart.value = new Chart(requestsCtx, {
          type: 'bar',
          data: {
            labels: ['Pending', 'In Progress', 'Completed', 'Cancelled'],
            datasets: [{
              label: 'Service Requests',
              data: [12, 19, 3, 5],
              backgroundColor: ['rgba(255, 206, 86, 0.2)', 'rgba(54, 162, 235, 0.2)', 'rgba(75, 192, 192, 0.2)', 'rgba(255, 99, 132, 0.2)'],
              borderColor: ['rgba(255, 206, 86, 1)', 'rgba(54, 162, 235, 1)', 'rgba(75, 192, 192, 1)', 'rgba(255, 99, 132, 1)'],
              borderWidth: 1
            }]
          }
        });
      }

      // Users Chart
      const usersCtx = document.querySelector('#usersChart');
      if (usersCtx) {
        usersChart.value = new Chart(usersCtx, {
          type: 'line',
          data: {
            labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
            datasets: [{
              label: 'New Users',
              data: [65, 59, 80, 81, 56, 55],
              fill: false,
              borderColor: 'rgb(75, 192, 192)',
              tension: 0.1
            }]
          }
        });
      }
    };
    downloadCSV();
    // Implement CSV download logic here
    fetch('/export').then(response => response.json()).then(data => console.log(data));
    alert('Download CSV clicked');
    const refreshUsers = async () => {
      loading.value = true;
      error.value = null;
      try {
        const userData = await adminAPI.getUsers();
        users.value = userData;
        updateStats();
      } catch (error) {
        console.error('Error refreshing users:', error);
        error.value = error.message || 'Failed to refresh users';
      } finally {
        loading.value = false;
      }
    };

    // Lifecycle hooks
    onMounted(() => {
      console.log('AdminDashboard mounted - loading data...');
      loadDashboardData();
      // Initialize charts after a delay to ensure DOM is ready
      setTimeout(() => {
        initCharts();
      }, 1000);
    });
    return {
      stats,
      users,
      services,
      currentTab,
      tabs,
      userSearch,
      serviceSearch,
      userTypeFilter,
      serviceForm,
      editingService,
      selectedUser,
      filteredUsers,
      filteredServices,
      pendingApprovals,
      loading,
      error,
      isDeleting,
      showNewServiceModal,
      editService,
      saveService,
      approveUser,
      toggleUserBlock,
      viewUserDetails,
      refreshUsers,
      loadDashboardData
    };
  },
  methods: {
    async handleDeleteService(serviceId) {
      if (!serviceId || typeof serviceId !== 'number') {
        console.error('Invalid service ID:', serviceId);
        return;
      }
      if (!confirm('Are you sure you want to delete this service?')) {
        return;
      }
      try {
        this.isDeleting = true;
        // Fix the API reference to use the imported adminAPI
        const result = await adminAPI.deleteService(serviceId);
        if (result.wasDeactivated) {
          // Check if $toast exists before using it
          if (this.$toast) {
            this.$toast.info(result.message || 'Service was deactivated');
          } else {
            alert(result.message || 'Service was deactivated');
          }

          // Update the service status in the list instead of removing it
          const serviceIndex = this.services.findIndex(s => s.id === serviceId);
          if (serviceIndex !== -1) {
            this.services[serviceIndex].is_active = false;
            this.services[serviceIndex].status = 'inactive';
          }
        } else {
          // Remove service from list
          this.services = this.services.filter(s => s.id !== serviceId);

          // Check if $toast exists before using it
          if (this.$toast) {
            this.$toast.success(result.message || 'Service deleted successfully');
          } else {
            alert(result.message || 'Service deleted successfully');
          }
        }
      } catch (error) {
        console.error('Delete failed:', error);
        // Check if $toast exists before using it
        if (this.$toast) {
          this.$toast.error(error.message || 'Failed to delete service');
        } else {
          alert(error.message || 'Failed to delete service');
        }
      } finally {
        this.isDeleting = false;
      }
    }
  }
};
